import React, {useState} from "react";
import { Navbar } from "./Navbar";
import DataTable from './DataTable';
import "./dashboard.css";
export const Dashboard = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedFilter, setSelectedFilter] = useState('all');
    const [filteredData, setFilteredData] = useState([]);
  
    // Your data array (replace with your actual data)
    const data = [
      {
        id: 1,
        name: 'Ram',
        email: 'ram@example.com',
        password: 'ram123',
      },
      {
        id: 2,
        name: 'Shyam',
        email: 'shyam@example.com',
        password: 'shyam123',
      }
    ];
  
    // Function to handle search input changes
    const handleSearchInputChange = (event) => {
      const query = event.target.value;
      setSearchQuery(query);
  
      // Filter the data based on the search query and selected filter
      const filteredResults = data.filter((item) =>
        (selectedFilter === 'all' || item.category === selectedFilter) &&
        item.name.toLowerCase().includes(query.toLowerCase())
      );
  
      setFilteredData(filteredResults);
    };
  
    // Function to handle filter selection changes
    const handleFilterChange = (event) => {
      const filterValue = event.target.value;
      setSelectedFilter(filterValue);
  
      // Filter the data based on the search query and selected filter
      const filteredResults = data.filter((item) =>
        (filterValue === 'all' || item.category === filterValue) &&
        item.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
  
      setFilteredData(filteredResults);
    };
  
    // Define filter options (replace with your options)
    const filterOptions = ['all', 'id','name','email'];

  return (
    <div className="data_table">
      <Navbar
      onSearchInputChange={handleSearchInputChange}
      filterOptions={filterOptions}
      onFilterChange={handleFilterChange}
      selectedFilter={selectedFilter}
      />
      
      <h2>Dashboard</h2>
      <div className="filter-dropdown">
          <label htmlFor="filter">Filter:</label>
          <select
            id="filter"
            onChange={onFilterChange}
            value={selectedFilter}
          >
            {filterOptions.map((option) => (
              <option key={option} value={option}>
                {option}
              </option>
            ))}
          </select>
        </div>
    </div>
  );
};
